INSERT INTO `tbl_pages` (`page_id`, `page_title`, `page_slug`, `page_contents`, `page_date`, `page_image`, `page_file_uploads`, `page_user_id`, `page_description`) VALUES
(1, 'Sejarah LPMP Sulawesi Selatan', 'sejarah-lpmp-sulawesi-selatan', '<p align=\"justify\">Surat Keputusan Menteri Pendidikan dan Kebudayaan RI nomor: 116/O/1977 tanggal 23 april 1977 tentang kedudukan, tugas, fungsi, susunan organisasi dan tata kerja Balai Penataran Guru (BPG) merupakan awal terbentuknya BPG. BPG Ujung Pandang, yang merupakan cikal bakal LPMP, diresmikan pada tanggal 27 april 1977 oleh Dirjen Kebudayaan Prof. Ida Bagus Mantra mewakili Menteri Pendidikan dan Kebudayaan disusul dengan pelantikan personil pimpinan dan staf pada tanggal 27 januari 1979 oleh Dirjen Pendidikan Dasar dan Menengah Prof. Dardji Darmodihardjo, SH berdasarkan SK Mendikbud Nomor: 78360/C/3/1978.</p><p align=\"justify\">Pada awal berdirinya tugas dan fungsi utama BPG adalah sebagai unit pelaksana teknis dalam peningkatan pendidikan melalui kegiatan pelatihan dan pendidikan. Namun tugas dan fungsi ini sebagaian diambil alih oleh bidang persekolahan Kanwil Depdikbud Tk. I Provinsi Sulawesi Selatan dan BPG hanya berfungsi sebagai wisma atau tempat pelatihan/penataran.</p><p align=\"justify\">Seiring perubahan yang terjadi terhadap paradigma pendidikan dan tuntutan masyarakat terhadap peningkatan mutu pendidikan maka pemerintah pusat melalui Departemen Pendidikan Nasional menetapkan sistem, standar mutu dan standar proses pendidikan. Sehubungan dengan hal itu maka Departemen Pendidikan Nasional mengeluarkan Surat Keputusan Menteri Pendidikan Nasional (KEPMENDIKNAS) nomor: 087/O/2003 tertanggal 4 juli 2003 tentang pendirian Lembaga Penjamin Mutu Pendidikan. Sejak diterbitkannya kepmendiknas tersebut maka BPG Ujungpandang secara resmi berubah menjadi Lembaga Penjamin Mutu Pendidikan (LPMP) Sulawesi Selatan.</p><p align=\"justify\">Berdasarkan Peraturan Menteri Pendidikan Nasional (Permendiknas) RI No. 7 Tahun 2007 disebutkan bahwa dalam rangka meningkatkan mutu pendidikan dasar dan menengah termasuk taman kanak-kanak (TK), raudatul athfal (RA), atau bentuk lain yang sederajat secara nasional sesuai dengan standar nasional pendidikan, perlu melakukan refungsionalisasi Lembaga Penjamin Mutu Pendidikan menjadi Lembaga Penjaminan Mutu Pendidikan.</p><p align=\"justify\">Kemudian, berdasarkan Permendikbud No.15 Tahun 2015 tentang organisasi Tata Kerja LPMP Sumatera Barat, LPMP Jawa Tengah dan LPMP Sulawesi Selatan (Eselon II) disebutkan bahwa LPMP mempunyai tugas melaksanakan penjaminan mutu, pengembangan model, dan kemitraan penjaminan mutu pendidikan dasar dan pendidikan menengah di provinsi.</p>', '2020-01-01 00:00:00', '', '', 1, 'Sejarah LPMP Sulawesi Selatan'),
(2, 'Visi dan Misi', 'visi-dan-misi', '<p><br> V I S I :</p><blockquote><p>\"&nbsp;MEWUJUDKAN PENJAMINAN MUTU PENDIDIKAN DASAR DAN MENENGAH SESUAI STANDAR NASIONAL PENDIDIKAN YANG BERLANDASKAN SEMANGAT GOTONG ROYONG DI PROVINSI SULAWESI SELATAN&nbsp;\"</p></blockquote><p>M I S I :</p><ol><li>Meningkatkan Penjaminan Mutu Pendidikan Dasar dan Menengah di Provinsi&nbsp; Sulawesi Selatan;</li><li>Mengembangkan Model Penjaminan Mutu Pendidikan Dasar dan Menengah di Provinsi&nbsp; Sulawesi Selatan;</li><li>Meningkatkan Pertisipasi Pemangku Kepentingan Pendidikan dalam Pelaksanaan Penjaminan Mutu Pendidikan Dasar dan Menengah di Provinsi&nbsp; Sulawesi Selatan;</li><li>Meningkatkan Tata Kelola Birokrasi Lembaga Penjaminan Mutu Pendidikan Sulawesi Selatan;</li></ol>', '2020-02-10 10:53:55', '', '', 1, 'Visi dan Misi LPMP Sulawesi Selatan'),
